+    public static String toString(ExecRow er) throws StandardException {
+        StringJoiner joiner = new StringJoiner(",");
+        for(int i = 1; i <= er.nColumns(); ++i) {
+            joiner.add(er.getColumn(i).toString());
+        }
+        return joiner.toString();
+    }
