+    @Test
+    public void testGetInputFormat()
+    {
+        Configuration configuration = new Configuration(false);
+
+        Properties avroSymlinkSchema = new Properties();
+        avroSymlinkSchema.setProperty(FILE_INPUT_FORMAT, SymlinkTextInputFormat.class.getName());
+        avroSymlinkSchema.setProperty(SERIALIZATION_LIB, AVRO.getSerDe());
+        assertTrue(getInputFormat(configuration, avroSymlinkSchema, false) instanceof SymlinkTextInputFormat);
+        assertTrue(getInputFormat(configuration, avroSymlinkSchema, true) instanceof AvroContainerInputFormat);
+
+        Properties parquetSymlinkSchema = new Properties();
+        parquetSymlinkSchema.setProperty(FILE_INPUT_FORMAT, SymlinkTextInputFormat.class.getName());
+        parquetSymlinkSchema.setProperty(SERIALIZATION_LIB, PARQUET.getSerDe());
+        assertTrue(getInputFormat(configuration, parquetSymlinkSchema, false) instanceof SymlinkTextInputFormat);
+        assertTrue(getInputFormat(configuration, parquetSymlinkSchema, true) instanceof MapredParquetInputFormat);
+
+        Properties parquetSchema = new Properties();
+        parquetSchema.setProperty(FILE_INPUT_FORMAT, MapredParquetInputFormat.class.getName());
+        assertTrue(getInputFormat(configuration, parquetSchema, false) instanceof MapredParquetInputFormat);
+        assertTrue(getInputFormat(configuration, parquetSchema, true) instanceof MapredParquetInputFormat);
+    }
