+        Iterator<?>[] values = inserts.stream().map(ParquetDecimalInsert::getValues).map(Iterable::iterator).toArray(Iterator[]::new);
+
+        createParquetFile(
+                output,
+                getStandardStructObjectInspector(columnNames, inspectors),
