    private static class CountingSendCallback
            implements Callback
    {
        private final AtomicLong remaining;
        private final CompletableFuture<?> future;

        private CountingSendCallback(long pageSize) {
            this.remaining = new AtomicLong(pageSize);
            this.future = new CompletableFuture();
        }

        @Override
        public void onCompletion(RecordMetadata metadata, Exception exception)
        {
            // TODO possibly handle exception here
            remaining.decrementAndGet();
            completeFutureIfDone();
        }

        private void completeFutureIfDone()
        {
            if (remaining.get() <= 0) {
                future.complete(null);
            }
        }

        public CompletableFuture<?> getFuture()
        {
            return future;
        }
    }

