+        this.messageEncoder = requireNonNull(messageEncoder, "messageEncoder is null");
+        this.session = requireNonNull(session, "session is null");
+        this.producer = requireNonNull(producerFactory.create(), "producerFactory is null");
+    }
+
+    @Override
+    public CompletableFuture<?> appendPage(Page page)
+    {
+        int partition;
+        for (int position = 0; position < page.getPositionCount(); position++) {
+            partition = 1;
+            for (int channel = 0; channel < page.getChannelCount(); channel++) {
+                if (!columns.get(channel).isInternal()) {
+                    if (columns.get(channel).isKeyDecoder()) {
+                        keyEncoder.putValueAt(columns.get(channel), session, page.getBlock(channel), position);
+                    }
+                    else {
+                        messageEncoder.putValueAt(columns.get(channel), session, page.getBlock(channel), position);
+                    }
+                }
+                else {
+                    partition = toIntExact(columns.get(channel).getType().getLong(page.getBlock(channel), position));
+                }
+            }
+            producer.send(new ProducerRecord<>(topicName, partition, keyEncoder.toByteArray(), messageEncoder.toByteArray()));
+            keyEncoder.clear();
+            messageEncoder.clear();
+        }
+        return NOT_BLOCKED;
+    }
+
+    @Override
+    public CompletableFuture<Collection<Slice>> finish()
+    {
+        producer.close();
+        return completedFuture(ImmutableList.of());
+    }
