+import static io.prestosql.spi.type.TimeType.TIME;
+import static io.prestosql.spi.type.TimeWithTimeZoneType.TIME_WITH_TIME_ZONE;
+import static io.prestosql.spi.type.TinyintType.TINYINT;
+import static io.prestosql.spi.type.VarbinaryType.isVarbinaryType;
+import static io.prestosql.spi.type.Varchars.isVarcharType;
+import static java.lang.Float.intBitsToFloat;
+import static java.lang.Math.toIntExact;
+import static java.lang.String.format;
+
+public interface RowEncoder
+{
+    byte[] toByteArray();
+
+    void clear();
+
+    default RowEncoder putValueFromBlock(EncoderColumnHandle columnHandle, ConnectorSession session, Block block, int position)
+    {
+        Type type = columnHandle.getType();
+        if (block.isNull(position)) {
+            return putNullValue(columnHandle);
+        }
+        else if (type == BOOLEAN) {
+            return putBoolean(columnHandle, type.getBoolean(block, position));
+        }
+        else if (type == BIGINT) {
+            return putLong(columnHandle, type.getLong(block, position));
+        }
+        else if (type == INTEGER) {
+            return putInt(columnHandle, toIntExact(type.getLong(block, position)));
+        }
+        else if (type == SMALLINT) {
+            return putShort(columnHandle, Shorts.checkedCast(type.getLong(block, position)));
+        }
+        else if (type == TINYINT) {
+            return putByte(columnHandle, SignedBytes.checkedCast(type.getLong(block, position)));
+        }
+        else if (type == DOUBLE) {
+            return putDouble(columnHandle, type.getDouble(block, position));
+        }
+        else if (type == REAL) {
+            return putFloat(columnHandle, intBitsToFloat(toIntExact(type.getLong(block, position))));
+        }
+        else if (isVarcharType(type)) {
+            return putString(columnHandle, type.getSlice(block, position).toStringUtf8());
+        }
+        else if (isVarbinaryType(type)) {
+            return putByteBuffer(columnHandle, type.getSlice(block, position).toByteBuffer());
+        }
+        else if (type == DATE) {
+            return putSqlDate(columnHandle, (SqlDate) type.getObjectValue(session, block, position));
+        }
+        else if (type == TIME) {
+            return putSqlTime(columnHandle, (SqlTime) type.getObjectValue(session, block, position));
+        }
+        else if (type == TIME_WITH_TIME_ZONE) {
+            return putSqlTimeWithTimeZone(columnHandle, (SqlTimeWithTimeZone) type.getObjectValue(session, block, position));
+        }
+        else if (type instanceof TimestampType) {
+            return putSqlTimestamp(columnHandle, (SqlTimestamp) type.getObjectValue(session, block, position));
+        }
+        else if (type instanceof TimestampWithTimeZoneType) {
+            return putSqlTimestampWithTimeZone(columnHandle, (SqlTimestampWithTimeZone) type.getObjectValue(session, block, position));
+        }
+        else {
+            throw new UnsupportedOperationException(format("Column '%s' does not support 'null' value", columnHandle.getName()));
+        }
+    }
+
+    default RowEncoder putNullValue(EncoderColumnHandle columnHandle)

