+package io.prestosql.tests.hive;
+
+import com.google.inject.AbstractModule;
+import com.google.inject.Module;
+import io.prestosql.tempto.configuration.Configuration;
+import io.prestosql.tempto.initialization.SuiteModuleProvider;
+
+import javax.inject.Inject;
+import javax.inject.Singleton;
+
+import java.sql.DatabaseMetaData;
+import java.sql.SQLException;
+import java.util.Optional;
+
+import static io.prestosql.tests.utils.QueryExecutors.onHive;
+
+public class HiveVersionProvider
+{
+    private Optional<HiveVersion> hiveVersion = Optional.empty();
+
+    @Inject
+    public HiveVersionProvider() {}
+
+    public HiveVersion getHiveVersion()
+    {
+        if (hiveVersion.isEmpty()) {
+            detectHiveVersion();
+        }
+        return hiveVersion.get();
+    }
+
+    private void detectHiveVersion()
+    {
+        try {
+            DatabaseMetaData metaData = onHive().getConnection().getMetaData();
+            int majorVersion = metaData.getDatabaseMajorVersion();
+            int minorVersion = metaData.getDatabaseMinorVersion();
+            this.hiveVersion = Optional.of(HiveVersion.createFromParts(majorVersion, minorVersion));
+        }
+        catch (SQLException e) {
+            throw new RuntimeException(e);
+        }
+    }
+
+    public static class ModuleProvider
+            implements SuiteModuleProvider
+    {
+        @Override
+        public Module getModule(Configuration configuration)
+        {
+            return new AbstractModule()
+            {
+                @Override
+                protected void configure()
+                {
+                    bind(HiveVersionProvider.class).in(Singleton.class);
+                }
+            };

