+    private void testBasicTableStatisticsForFormat(Session session, FileFormat format)
+    {
+        String tableName = format("iceberg.tpch.test_basic_%s_table_statistics", format.name().toLowerCase(ENGLISH));
+        assertUpdate(format("CREATE TABLE %s (col REAL) WITH (format = '%s')", tableName, format.name().toLowerCase(ENGLISH)));
+        String insertStart = format("INSERT INTO %s", tableName);
+        assertUpdate(session, insertStart + " VALUES -10", 1);
+        assertUpdate(session, insertStart + " VALUES 100", 1);
+
+        TableStatistics tableStatistics = getTableStatistics(tableName, Constraint.alwaysTrue());
+        assertEquals(2.0, tableStatistics.getRowCount().getValue());
+        ColumnStatistics columnStatistics = getStatisticsForColumn(tableStatistics, "col", format);
+        assertEquals(new DoubleRange(-10, 100), columnStatistics.getRange().get());
+
+        assertUpdate(session, insertStart + " VALUES 200", 1);
+
+        tableStatistics = getTableStatistics(tableName, Constraint.alwaysTrue());
+        columnStatistics = checkColumnStatistics(getStatisticsForColumn(tableStatistics, "col", format), format);
+        assertEquals(new DoubleRange(-10, 200), columnStatistics.getRange().get());
+
+        dropTable(session, tableName);
+    }
