+    private void testMultipleColumnTableStatisticsForFormat(Session session, FileFormat format)
+    {
+        String tableName = format("iceberg.tpch.test_multiple_%s_table_statistics", format.name().toLowerCase(ENGLISH));
+        assertUpdate(format("CREATE TABLE %s (col1 REAL, col2 INTEGER, col3 DATE) WITH (format = '%s')", tableName, format.name()));
+        String insertStart = format("INSERT INTO %s", tableName);
+        assertUpdate(session, insertStart + " VALUES (-10, -1, DATE '2019-06-28')", 1);
+        assertUpdate(session, insertStart + " VALUES (100, 10, DATE '2020-01-01')", 1);
+
+        TableStatistics tableStatistics = getTableStatistics(tableName, Constraint.alwaysTrue());
+        assertEquals(2.0, tableStatistics.getRowCount().getValue());
+
+        ColumnStatistics columnStatistics = getStatisticsForColumn(tableStatistics, "col1", format);
+        assertEquals(new DoubleRange(-10, 100), columnStatistics.getRange().get());
+
+        columnStatistics = getStatisticsForColumn(tableStatistics, "col2", format);
+        assertEquals(new DoubleRange(-1, 10), columnStatistics.getRange().get());
+
+        columnStatistics = getStatisticsForColumn(tableStatistics, "col3", format);
+        LocalDate lowDate = LocalDate.of(2019, 6, 28);
+        LocalDate highDate = LocalDate.of(2020, 1, 1);
+        assertEquals(new DoubleRange(lowDate.toEpochDay(), highDate.toEpochDay()), columnStatistics.getRange().get());
+
+        assertUpdate(session, insertStart + " VALUES (200, 20, DATE '2020-06-28')", 1);
+
+        tableStatistics = getTableStatistics(tableName, Constraint.alwaysTrue());
+        assertEquals(3.0, tableStatistics.getRowCount().getValue());
+
+        columnStatistics = getStatisticsForColumn(tableStatistics, "col1", format);
+        assertEquals(new DoubleRange(-10, 200), columnStatistics.getRange().get());
+
+        columnStatistics = getStatisticsForColumn(tableStatistics, "col2", format);
+        assertEquals(new DoubleRange(-1, 20), columnStatistics.getRange().get());
+
+        columnStatistics = getStatisticsForColumn(tableStatistics, "col3", format);
+        highDate = LocalDate.of(2020, 6, 28);
+        assertEquals(new DoubleRange(lowDate.toEpochDay(), highDate.toEpochDay()), columnStatistics.getRange().get());
+
+        assertUpdate(insertStart + " VALUES " + IntStream.rangeClosed(21, 25)
+                .mapToObj(i -> format("(200, %d, DATE '2020-07-%d')", i, i))
+                .collect(Collectors.joining(", ")), 5);
+
+        assertUpdate(insertStart + " VALUES " + IntStream.rangeClosed(26, 30)
+                .mapToObj(i -> format("(NULL, %d, DATE '2020-06-%d')", i, i))
+                .collect(Collectors.joining(", ")), 5);
+
+        tableStatistics = getTableStatistics(tableName, Constraint.alwaysTrue());
+        assertEquals(tableStatistics.getRowCount().getValue(), 13.0);
+        columnStatistics = getStatisticsForColumn(tableStatistics, "col1", format);
+        assertEquals(columnStatistics.getNullsFraction().getValue(), 5.0 / 13.0);
+
+        dropTable(session, tableName);
+    }
