+    private static final String HIVE_CATALOG = "hive";
+    private File catalogDirectory = Files.createTempDir();
+
+    @AfterClass(alwaysRun = true)
+    public void close()
+    {
+        try {
+            deleteRecursively(catalogDirectory.toPath(), ALLOW_INSECURE);
