+    @Override
+    public ConnectorInsertTableHandle beginRefreshMaterializedView(ConnectorSession session, ConnectorTableHandle tableHandle, List<ConnectorTableHandle> sourceTableHandles)
+    {
+        IcebergTableHandle table = (IcebergTableHandle) tableHandle;
+        org.apache.iceberg.Table icebergTable = getIcebergTable(metastore, hdfsEnvironment, session, table.getSchemaTableName());
+        transaction = icebergTable.newTransaction();
+
+        // delete before insert .. simulating overwrite
+        executeDelete(session, tableHandle);
+
+        // Grab the snapshot-ids of the source table. The table scans for insert are planned and
+        // executed as of these snapshots and these snapshot ids are written to storage table
+        // as 'dependsOnTables' property. This 'as-of' processing ensures that the metadata of the
+        // materialized view and the data are in sync.
+        Map<ConnectorTableHandle, Long> tableSnapshotIdMap = new HashMap<>();
+        for (ConnectorTableHandle handle : sourceTableHandles) {
+            Optional<io.prestosql.plugin.iceberg.TableToken> token = getTableToken(session, handle);
+            if (token.isPresent()) {
+                tableSnapshotIdMap.put(handle, token.get().getSnapshotId());
+            }
+            else {
+                tableSnapshotIdMap.put(handle, (long) -1);
+            }
+        }
+        transactionHandle.setTableSnapshotIdMap(tableSnapshotIdMap);
+        return new IcebergWritableTableHandle(
+            table.getSchemaName(),
+            table.getTableName(),
+            SchemaParser.toJson(icebergTable.schema()),
+            PartitionSpecParser.toJson(icebergTable.spec()),
+            getColumns(icebergTable.schema(), typeManager),
+            getDataPath(icebergTable.location()),
+            getFileFormat(icebergTable));
+    }
+
+    @Override
+    public Optional<ConnectorOutputMetadata> finishRefreshMaterializedView(ConnectorSession session,
+            ConnectorInsertTableHandle insertHandle,
+            Collection<Slice> fragments,
+            Collection<ComputedStatistics> computedStatistics,
+            List<ConnectorTableHandle> sourceTableHandles)
+    {
+        IcebergWritableTableHandle table = (IcebergWritableTableHandle) insertHandle;
+        org.apache.iceberg.Table icebergTable = transaction.table();
+        List<CommitTaskData> commitTasks = new ArrayList<>();
+
+        if (!fragments.isEmpty()) {
+            commitTasks = fragments.stream()
+                .map(slice -> commitTaskCodec.fromJson(slice.getBytes()))
+                .collect(toImmutableList());
+
+            Type[] partitionColumnTypes = icebergTable.spec().fields().stream()
+                .map(field -> field.transform().getResultType(
+                    icebergTable.schema().findType(field.sourceId())))
+                .toArray(Type[]::new);
+
+            AppendFiles appendFiles = transaction.newFastAppend();
+            for (CommitTaskData task : commitTasks) {
+                DataFiles.Builder builder = DataFiles.builder(icebergTable.spec())
+                        .withInputFile(HadoopInputFile.fromLocation(task.getPath(), getConfiguration(session, table.getSchemaName())))
+                        .withFormat(table.getFileFormat())
+                        .withMetrics(task.getMetrics().metrics());
+
+                if (!icebergTable.spec().fields().isEmpty()) {
+                    String partitionDataJson = task.getPartitionDataJson()
+                            .orElseThrow(() -> new VerifyException("No partition data for partitioned table"));
+                    builder.withPartition(PartitionData.fromJson(partitionDataJson, partitionColumnTypes));
+                }
+
+                appendFiles.appendFile(builder.build());
+            }
+
+            // Update the map that tracks tables on which the materialized view depends and the corresponding snapshot ids of the table
+            String tableSnapshotIDString = Joiner.on(",").withKeyValueSeparator("=").join(transactionHandle.getTableSnapshotIdMap());
+            appendFiles.set(DEPENDS_ON_TABLES, tableSnapshotIDString);
+            appendFiles.commit();
+        }
+        transaction.commitTransaction();
+        return Optional.of(new HiveWrittenPartitions(commitTasks.stream()
+            .map(CommitTaskData::getPath)
+            .collect(toImmutableList())));
+    }
