+import static io.prestosql.SystemSessionProperties.JOIN_REORDERING_STRATEGY;
+import static io.prestosql.plugin.hive.HiveQueryRunner.HIVE_PARTITIONED_CATALOG;
+import static io.prestosql.plugin.hive.HiveQueryRunner.TPCH_PARTITIONED_SCHEMA;
+import static io.prestosql.plugin.hive.HiveQueryRunner.TPCH_SCHEMA;
