                    return ImmutableList.copyOf(readPages(SERDE, new BasicSliceInput(data.getDataSource())));



                }







                @State(Scope.Thread)



                public static class BenchmarkData



                {



                    private final Random random = new Random(0);



                    private File temporaryDirectory;



                    private File file;



                    private Slice dataSource;







                    @Setup



                    public void setup()



                            throws Exception



                    {



                        temporaryDirectory = createTempDir();



                        file = new File(temporaryDirectory, randomUUID().toString());



                        BlockBuilder blockBuilder = BIGINT.createBlockBuilder(null, 1024);



                        Iterator<?> values = createValues();



                        while (values.hasNext()) {



                            Object value = values.next();



                            if (value == null) {



                                blockBuilder.appendNull();



                            }



                            else {



                                BIGINT.writeLong(blockBuilder, ((Number) value).longValue());



                            }



                        }



                        Page page = new Page(blockBuilder.build());



                        writePages(SERDE, new OutputStreamSliceOutput(new FileOutputStream(file)), page);



                        dataSource = Slices.wrappedBuffer(readAllBytes(file.toPath()));



                    }







                    public Slice getDataSource()



                    {



                        return dataSource;



                    }







                    protected Iterator<?> createValues()



                    {



                        List<Long> values = new ArrayList<>();



                        for (int i = 0; i < ROWS; ++i) {



                            if (random.nextBoolean()) {



                                values.add(random.nextLong());



                            }



                            else {



                                values.add(null);



                            }



                        }



                        return values.iterator();



                    }



                }







                @Test



                public void testReadLongWithNull()



                        throws Exception



                {



                    BenchmarkPagesSerde benchmark = new BenchmarkPagesSerde();



                    BenchmarkData data = new BenchmarkData();



                    data.setup();



                    benchmark.readLongWithNull(data);



                }







