                        throws Exception



                {



                    Options options = new OptionsBuilder()



                            .verbosity(VerboseMode.NORMAL)



                            .include(".*" + BenchmarkPagesSerde.class.getSimpleName() + ".*")



                            .build();







                    new Runner(options).run();



                }



            }

