+ *                   - Project (marker1 <- null, marker2 <- true, marker3 <- null)
+ *                       - Source2 (a2, b2)
+ *                   - Project (marker1 <- null, marker2 <- null, marker3 <- true)
+ *                       - Source3 (a3, b3)
