+/** Lobby chat history records lobby chat messages. */
+public interface LobbyChatHistoryDao {
+  int MESSAGE_COLUMN_LENGTH = 240;
+
+  default void recordMessage(ChatReceivedMessage chatReceivedMessage, int apiKeyId) {
+    insertMessage(
