+        else {
+            Spark.halt(HttpStatus.SC_BAD_REQUEST);
+        }
+        return json(response, ResultJson.success());
+    }
+
+    private static void handleRescheduleResult(List<RescheduleResult> results, String strategy) {
+        results.forEach(result -> {
