+    for (size_t idx = type.dimensions().size(); idx-- > 0; ) {
+        if ((labels[idx] += 1.0) < type.dimensions()[idx].size) {
+            return true;
+        } else {
+            labels[idx] = 0.0;
+        }
+    }
