+    fn internals(&self) -> (usize, usize);
+    fn clone_dyn(&self) -> Box<dyn Closure<Args, Output>>;
+    fn eq_dyn(&self, other: &dyn Closure<Args, Output>) -> bool;
+    fn cmp_dyn(&self, other: &dyn Closure<Args, Output>) -> Ordering;
