                private List<String> getSupportedLocales(Iterator<Locale> fallbackLocalesIter) {



                    if (fallbackLocalesIter != null && fallbackLocalesIter.hasNext()) {



                        String locale = fallbackLocalesIter.next().toLanguageTag();



                        Map<String, String> supportedLocales = RemoteLocaleOpt.getSupportedLanguages(locale);



                        if (supportedLocales == null || supportedLocales.isEmpty()) {



                            return getSupportedLocales(fallbackLocalesIter);



                        }



                        return supportedLocales.keySet();



                    }



