+                String fileName = file.getFileName().toString();
+                if(fileName.startsWith(BUNDLE_PREFIX) && fileName.endsWith(BUNDLE_SUFFIX)) {
+                    return fileName.substring(BUNDLE_PREFIX.length(), fileName.indexOf('.'));
+                }
