+import React from 'react';
+import API from 'AppData/api';
+import PropTypes from 'prop-types';
+import { FormattedMessage } from 'react-intl';
+import DialogContentText from '@material-ui/core/DialogContentText';
+import DeleteForeverIcon from '@material-ui/icons/DeleteForever';
+import FormDialogBase from 'AppComponents/AdminPages/Addons/FormDialogBase';
+
+/**
+ * Render delete dialog box.
+ * @param {JSON} props component props.
+ * @returns {JSX} Loading animation.
+ */
+function Delete({ updateList, dataRow }) {
+    const { id } = dataRow;
+
+    const formSaveCallback = () => {
+        const promiseAPICall = new Promise((resolve, reject) => {
+            const restApi = new API();
+            restApi
+                .deleteMicrogatewayLabel(id)
+                .then(() => {
+                    resolve(
+                        <FormattedMessage
+                            id='AdminPages.Microgateways.Delete.form.delete.successful'
+                            defaultMessage='Microgateway Label deleted successfully'
+                        />,
+                    );
+                })
+                .catch((error) => {
+                    reject(error.response.body.description);
+                })
+                .finally(() => {
+                    updateList();
+                });
+        });
+
+        return promiseAPICall;
+    };
+
+    return (
+        <FormDialogBase
+            title='Delete Microgateway Label?'
+            saveButtonText='Delete'
+            icon={<DeleteForeverIcon />}
+            formSaveCallback={formSaveCallback}
+        >
+            <DialogContentText>Are you sure you want to delete this Microgateway Label?</DialogContentText>
