+         *
+         */
+        return null;
+    }
+
+    private void appendURLContext(StringBuilder serverURL, String urlContext) {
+
+        if (!serverURL.toString().endsWith("/") && urlContext.trim().charAt(0) != '/') {
+            serverURL.append("/").append(urlContext.trim());
+        } else if (serverURL.toString().endsWith("/") && urlContext.trim().charAt(0) == '/') {
+            serverURL.append(urlContext.trim().substring(1));
+        } else {
+            serverURL.append(urlContext.trim());
+        }
+    }
+
+    public String getAbsoluteUrl() throws URLResolverException {
+
+        StringBuilder absoluteUrl = new StringBuilder();
+        String protocol = this.getProtocol();
+        if (StringUtils.isBlank(protocol)) {
+            protocol = this.fetchProtocol();
+        }
+        absoluteUrl.append(protocol).append("://");
+        String hostName = this.getHostName();
+        if (StringUtils.isBlank(hostName)) {
+            hostName = this.fetchHostName();
+        }
+        if (hostName.endsWith("/")) {
+            hostName = hostName.substring(0, hostName.length() - 1);
+        }
+        absoluteUrl.append(hostName.toLowerCase());
+
+        Integer port = this.getPort();
+        if (port == null) {
+            port = this.fetchPort();
+        }
+        if (port != IdentityCoreConstants.DEFAULT_HTTPS_PORT) {
+            absoluteUrl.append(":").append(port);
+        }
+        appendContext(absoluteUrl, this.getUrlContext());
+        return absoluteUrl.toString();
+    }
