+    private static final String MESSAGE_PREFIX = "pscanalpha.corp.";
+
+    private static final int PLUGIN_ID = 90004;
+    public static final String CROSS_ORIGIN_RESOURCE_POLICY_HEADER = "Cross-Origin-Resource-Policy";
