+    private final SiteIsolationHeaderScanner[] scanners =
+            new SiteIsolationHeaderScanner[] {
+                new CorpHeaderScanner(this::newAlert),
+                new CoepHeaderScanner(this::newAlert),
+                new CoopHeaderScanner(this::newAlert)
