+    abstract static class SiteIsolationHeaderScanner {
+        private final Supplier<AlertBuilder> newAlert;
+
+        public SiteIsolationHeaderScanner(Supplier<AlertBuilder> newAlert) {
+            this.newAlert = newAlert;
