+	@Test
+	public void radioTest() {
+		connect();
+		
+		JQuery radio = jq(".z-radio").eq(0);
+		JQuery radioDisabledButton = jq("@button").eq(0);
+		
+		Assert.assertTrue(radio.hasClass("z-radio-off"));
+		
+		click(jq(".z-radio-content").eq(0));
+		waitResponse();
+		Assert.assertTrue(radio.hasClass("z-radio-on"));
+		
+		click(jq(".z-radio-content").eq(1));
+		waitResponse();
+		Assert.assertFalse(radio.hasClass("z-radio-on"));
+		Assert.assertTrue(jq(".z-radio").eq(1).hasClass("z-radio-on"));
+		
+		click(radioDisabledButton);
+		waitResponse();
+		Assert.assertTrue(radio.hasClass("z-radio-disabled"));
+		
+		click(radioDisabledButton);
+		waitResponse();
+		Assert.assertFalse(radio.hasClass("z-radio-disabled"));
+	}
