+       */
+    public async openDropdown() {
+      const is = await this.isOpen();
+      if (!is) {
+            // click bars icon to open dropdown
+        const icon = await findIcon(this.page, '', 'bars');
+        await icon.click();
+        await this.page.waitForXPath(this.angleIconXpath, {timeout: 2000});
