+				classLoader = new URLClassLoader(
+					new URL[]{PackagedProgramUtils.getPythonJar()},
+					Thread.currentThread().getContextClassLoader());
+			} catch (RuntimeException e) {
+				LOG.warn(
+					"An attempt to load the flink-python jar from the \"opt\" directory failed, " +
+						"fall back to use the context class loader to reflect the flink-python class.", e);
+				classLoader = Thread.currentThread().getContextClassLoader();
+			}
+			Class<?> utilClazz = Class.forName(
+				"org.apache.flink.python.util.PythonDependencyUtils",
