ArgumentCaptor<String[]> enabledProtocols = ArgumentCaptor.forClass(String[].class);
verify(engine).setEnabledProtocols(enabledProtocols.capture());
assertThat(enabledProtocols.getValue()).containsExactly("someProtocol");

ArgumentCaptor<String[]> enabledCipherSuites = ArgumentCaptor.forClass(String[].class);
verify(engine). setEnabledCipherSuites(enabledCipherSuites.capture());
assertThat(enabledCipherSuites.getValue()).containsExactly("someCipher");

