    @Override
    protected Object randomValue(Type.PrimitiveType primitive, Random rand) {
      this.rowCount += 1;
      if (rowCount > dictionaryEncodedRows) {
        return generatePrimitive(primitive, rand);
      } else {
        return generateDictionaryEncodablePrimitive(primitive, rand);
      }
    }
  }

