+        /// <summary>
+        /// Tests that commit applies cache changes.
+        /// </summary>
+        [Test]
+        public void TestTxCommit()
+        {
+            var cache = GetTransactionalCache();
+
+            cache.Put(1, 1);
+            cache.Put(2, 2);
+
+            using (var tx = Client.GetTransactions().TxStart())
+            {
+                cache.Put(1, 10);
+                cache.Put(2, 20);
+
+                tx.Commit();
+            }
+
+            Assert.AreEqual(10, cache.Get(1));
+            Assert.AreEqual(20, cache.Get(2));
+        }
