+  private static ConcurrentLinkedQueue<RealtimeLuceneReadersForRealtimeSegment> _luceneRealtimeReaders;
+
+  private RealtimeLuceneIndexRefreshState() {
+    // This constructor is called by getInstance() exactly once. Since
+    // getInstance() is invoked by MutableSegmentImpl only if it encounters
+    // a TEXT index column, we ensure that background refresh thread is not created
+    // if there is no column with TEXT index enabled
+    _scheduledExecutorService = Executors.newSingleThreadScheduledExecutor();
+    // TODO: eventually we should explore partitioning this queue on per table basis
+    _luceneRealtimeReaders = new ConcurrentLinkedQueue<>();
+    _scheduledExecutorService.scheduleWithFixedDelay(new RealtimeLuceneIndexReaderRefreshThread(_luceneRealtimeReaders),
+        RealtimeLuceneIndexReaderRefreshThread.INITIAL_DELAY_MS_DEFAULT, RealtimeLuceneIndexReaderRefreshThread.DELAY_BETWEEN_SUCCESSIVE_EXECUTION_MS_DEFAULT, TimeUnit.MILLISECONDS);
+  }
+
+  public static RealtimeLuceneIndexRefreshState getInstance() {
+    if (_singletonInstance == null) {
+      synchronized (RealtimeLuceneIndexRefreshState.class) {
+        if (_singletonInstance == null) {
