+            }).exceptionally(e -> {
+                if (log.isDebugEnabled()) {
+                    log.debug("Fail to publish message: " + e.getMessage());
+                }
+                asyncResponse.resume(new RestException(Status.INTERNAL_SERVER_ERROR, "Fail to publish message:"
+                        + e.getMessage()));
+                return null;
+            });
+        }
+    }
