+        assertThat(od, hasSize(3));
+
+        // Now that 2 is a dependency of 1, there is a strict order required
+        assertEquals(service2, od.get(0));
+        assertEquals(service1, od.get(1));
+        assertEquals(mockMain, od.get(2));
