+
+    @Argument(fullName = "keep-duplex-only", doc = "Discard all duplicate sets that don't have duplex evidence")
+    public boolean duplexOnly = false;
+
+    private static final int RANDOM_SEED = 142;
+    private RandomGenerator rng;
