+  public GrpcErrorMapper(final Logger logger) {
+    this.logger = logger;
+  }
+
+  public StatusRuntimeException mapError(final Throwable error) {
+    return StatusProto.toStatusRuntimeException(mapErrorToStatus(error));
+  }
