+			controlVariable.setValue((double) getCurrentIteration());
+			executedCount++;
+
+			if (isConditionTrue()) {
+				return true;
+			}
+
+			isCurrentLoopInitialized = false;
+			if (action != null) {
+				action.restart();
+			}
+		}
+		return false;
+	}
+
+	@Override
+	public void restart() {
+		isCurrentLoopInitialized = false;
