+package org.catrobat.catroid.content.actions
+
+import android.app.Activity
+import android.content.Intent
+import android.speech.RecognizerIntent
+import android.util.Log
+import com.badlogic.gdx.scenes.scene2d.Action
+import org.catrobat.catroid.content.Sprite
+import org.catrobat.catroid.formulaeditor.Formula
+import org.catrobat.catroid.formulaeditor.InterpretationException
+import org.catrobat.catroid.formulaeditor.UserVariable
+import org.catrobat.catroid.stage.StageActivity
+import org.catrobat.catroid.stage.StageActivity.IntentListener
+import java.util.ArrayList
+
+class AskSpeechAction : Action(), IntentListener {
+
+    private var sprite: Sprite? = null
+    private var questionFormula: Formula? = null
+    private var answerVariable: UserVariable? = null
+    private var questionAsked = false
+    private var answerReceived = false
+
+    private fun askQuestion() {
+        if (StageActivity.messageHandler == null) {
+            return
+        }
+        val params = ArrayList<Any>()
+        params.add(this)
+        val message = StageActivity.messageHandler.obtainMessage(StageActivity.REGISTER_INTENT, params)
+        message.sendToTarget()
+
+        questionAsked = true
+    }
+
+    private fun createRecognitionIntent(question: String?): Intent {
+        val intent = Intent(RecognizerIntent.ACTION_RECOGNIZE_SPEECH)
+        intent.putExtra(
+            RecognizerIntent.EXTRA_LANGUAGE_MODEL,
+            RecognizerIntent.LANGUAGE_MODEL_FREE_FORM
+        )
+        if (question != null && question.length != 0) {
+            intent.putExtra(RecognizerIntent.EXTRA_PROMPT, question)
+        }
+        return intent
+    }
+
+    fun setAnswerText(answer: String?) {
+        if (answerVariable == null) {
+            return
+        }
+        answerVariable!!.value = answer
+
+        answerReceived = true
+    }
+
+    fun setAnswerVariable(answerVariable: UserVariable?) {
+        if (answerVariable == null) {
+            return
+        }
+        this.answerVariable = answerVariable
+    }
+
+    fun setQuestionFormula(questionFormula: Formula?) {
+        this.questionFormula = questionFormula
+    }
+
+    fun setSprite(sprite: Sprite?) {
+        this.sprite = sprite
+    }
+
+    override fun act(delta: Float): Boolean {
+        if (!questionAsked) {
+            askQuestion()
+        }
+        return answerReceived
+    }
+
+    override fun restart() {
+        questionAsked = false
+        answerReceived = false
+        super.restart()
+    }
+
+    override fun getTargetIntent(): Intent {
+        var question: String? = ""
+        try {
+            if (questionFormula != null) {
+                question = questionFormula!!.interpretString(sprite)
+            }
+        } catch (e: InterpretationException) {
+            Log.e(javaClass.simpleName, "formula interpretation in ask brick failed")
+        }
+        return createRecognitionIntent(question)
+    }
+
+    override fun onIntentResult(resultCode: Int, data: Intent) {
+        val matches: ArrayList<String>?
+        when (resultCode) {
+            Activity.RESULT_OK -> {
+                matches = data.getStringArrayListExtra(RecognizerIntent.EXTRA_RESULTS)
+                Log.d(TAG, "Results Speechrecognition: $matches")
+                if (matches != null && matches.size > 0) {
+                    setAnswerText(matches[0])
+                } else {
+                    setAnswerText("")
+                }
+            }
+            Activity.RESULT_CANCELED, Activity.RESULT_FIRST_USER -> setAnswerText("") // User canceled action
+            else -> Log.e(TAG, "unhandled speech recognizer resultCode $resultCode")
+        }
