+    public void testIsValidFailureIss() {
+        String json = new StringBuilder()
+            .append("{")
+                .append("\"aud\":\"").append(clientId).append("\",")
+                .append("\"iss\":\"").append("invalid_issuer").append("\"")
+            .append("}")
+            .toString();
+
+        String token = createJwt(json);
+
+        service.isValid(token);
+    }
