+      FieldType.Type fieldType = fieldTypes.get(sortField);
+
+      Object left = null;
+      Object right = null;
+      switch (fieldType) {
+        case STRING:
+          left = o1.getString(sortField);
+          right = o2.getString(sortField);
+          break;
+        case INTEGER:
+          left = o1.getInteger(sortField);
+          right = o2.getInteger(sortField);
+          break;
+        case LONG:
+          left = o1.getLong(sortField);
+          right = o2.getLong(sortField);
+          break;
+        case FLOAT:
+          left = o1.getFloat(sortField);
+          right = o2.getFloat(sortField);
+          break;
+        case DOUBLE:
+          left = o1.getDouble(sortField);
+          right = o2.getDouble(sortField);
