const extraLabels = [
              {
              label: 'NOT AND',
              value: 'NOT AND',
            },
            {
              label: 'NOT OR',
              value: 'NOT OR',
            },
]

const showExtraLabels = !this.options || this.options.isFormBuilder !== true

const labels = {
          list: [
            {
              label: 'AND',
              value: 'AND',
            },
            {
              label: 'OR',
              value: 'OR',
            },
            ...(showExtraLabels ? extraLabels : [])
        ]
}

