+        if (columns.length == 0) {
+            return null;
+        }
+
+        final StringBuilder result = new StringBuilder();
+        for (int i = 0; i < columns.length; ++i) {
+            final String column = columns[i];
+            result.append(DATABASE.qualifiedTableName(tableName) + "." + column);
+            if ((i + 1) < columns.length) {
