+                {
+                    try
+                    {
+                        bindEventParamsForInsert( ps, event );
+                    }
+                    catch ( JsonProcessingException | SQLException e )
+                    {
+                        log.warn( "PSI failed to persist and will be ignored. PSI UID: " + event.getUid(),
+                            event.getUid(), e );
+                    }
+                }
+
+                @Override
