+            glRegDeps->addChildren(&temp, 1);
+            }
+         }
+      }
+   prevBlock->append(TR::TreeTop::create(comp, ifacmpeqNode));
+
+   comp->dumpMethodTrees("Trees after inserting acmpeq fast path");
+   }

