+  static List<Either<SymbolInformation, DocumentSymbol>>collectSymbols(MyDocumentModel documentModel) {
+    if (documentModel == null || documentModel.getAnalysisResult().getOutlineTree() == null) {
+      return Collections.emptyList();
+    }
+    return documentModel.getAnalysisResult().getOutlineTree().getOutlineNodes().stream()
+        .map(OutlineService::convert)
+        .map(Either::<SymbolInformation, DocumentSymbol>forRight)
+        .collect(Collectors.toList());
