+        convert(outlineNode.getOutlineRange()),
+        "",
+        outlineNode.getNestedNodes().stream()
+            .map(OutlineService::convert)
+            .collect(Collectors.toList())
+    );
+  }
+
+  static SymbolKind convert(NodeType nodeType) {
+    switch (nodeType) {
+      case PROGRAM:
+        return SymbolKind.Class;
+      case DIVISION:
+      case PROCEDURE:
+      case PROCEDURE_SECTION:
+      case PROGRAM_ID:
+        return SymbolKind.Method;
+      case FIELD:
+      case FIELD_66:
+        return SymbolKind.Variable;
+      case FIELD_88:
+        return SymbolKind.EnumMember;
+      case STRUCT:
+        return SymbolKind.Struct;
+      case SECTION:
+        return SymbolKind.Object;
+      case FILE:
+      case COPYBOOK:
+        return SymbolKind.File;
+      case REDEFINES:
+        return SymbolKind.Constant;
+      default:
+        return SymbolKind.Null;
+    }
+  }
+
+  static Range convert(OutlineRange outlineRange) {
+    return new Range(convert(outlineRange.getStart()), convert(outlineRange.getEnd()));
+  }
+
+  static Position convert(OutlinePosition outlinePosition) {
+    return new Position(outlinePosition.getLine() - 1, outlinePosition.getCharacter());
