    dh.getUrl("/version", res -> context.verify(h -> {
      Assume.assumeTrue(res.succeeded());  // skip test if no access to Okapi
      context.assertTrue(res.result().containsKey("Version"));
      async.complete();
    }));
    async.await(1000);

