+    }
+
+    @Nonnull
+    public static AliasMap empty() {
+        return new AliasMap(ImmutableBiMap.of());
+    }
+
+    @Nonnull
+    public static AliasMap of(@Nonnull final CorrelationIdentifier source, @Nonnull final CorrelationIdentifier target) {
