+			consensusStart := recordFile.consensus_start;
+			consensusEnd := recordFile.consensus_end;
+
+			if currentNs is NOT NULL and currentNs >= consensusStart then
+				txnCount := 1;
+			end if;
+
+			while true
+				loop
